#include <bits/stdc++.h>

using namespace std;

class Solution {
public:
    char findTheDifference(string s, string t) {
        char answer;
        int n = s.size();
        int d = (int) t[t.size() - 1];
        for (int i = 0; i < n; ++i) {
            int s1 = (int) s[i];
            int t1 = (int) t[i];
            d ^= s1;
            d ^= t1;
        }

        answer = (char) d;

        return answer;
    }
};

int main() {
    string s, t;
    char answer;
    Solution solve;

    s = "";
    t = "";
    answer = 'y';
    assert(solve.findTheDifference(s, t) == answer);

    s = "", t = "y";
    answer = 'y';
    assert(solve.findTheDifference(s, t) == answer);

    s = "abcd", t = "abcde";
    answer = 'e';
    assert(solve.findTheDifference(s, t) == answer);

    return 0;
}
